begin
    if(load)
    begin
        case(amount)
            2'b00: q <= data << 1;
            2'b01: q <= data << 8;
            2'b10: q <= data >> 1;
            2'b11: q <= data >> 8;
            default: q <= data;
        endcase
    end
end

endmodule

