module signed_adder(
    input [7:0] a,
    input [7:0] b,
    output [7:0] s,
    output overflow
);
    wire [8:0] sum; // 9-bit wire to capture the carry out

    // Perform the addition
    assign sum = {a[7], a} + {b[7], b};

    // Assign the result to the output
    assign s = sum[7:0];

    // Capture overflow: occurs if the sign bit of the inputs are the same and the sign bit of the result is different
    assign overflow = (a[7] == b[7]) && (s[7] != a[7]);

endmodule
