module signed_adder(
    input [7:0] a,
    input [7:0] b,
    output [7:0] s,
    output overflow
);
    wire [7:0] sum;
    wire carry_out_a, carry_out_b;

    // Perform the addition
    assign sum = a + b;

    // Assign the result to the output
    assign s = sum;

    // Calculate the carry out from the MSB
    assign carry_out_a = a[7] & b[7];
    assign carry_out_b = ~sum[7] & (a[7] | b[7]);

    // Detect overflow
    assign overflow = carry_out_a | carry_out_b;

endmodule
