module adv_fsm(
    input clk,
    input reset,
    input x,
    output reg z
);
    reg [1:0] present_state, next_state;
    parameter IDLE = 2'b00, S1 = 2'b01, S10 = 2'b10, S101 = 2'b11;

    // State transition logic
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            present_state <= IDLE;
        end else begin
            present_state <= next_state;
        end
    end

    // Next state logic
    always @(*) begin
        case (present_state)
            IDLE: begin
                if (x) 
                    next_state = S1;
                else 
                    next_state = IDLE;
            end
            S1: begin
                if (~x) 
                    next_state = S10;
                else 
                    next_state = IDLE;
            end
            S10: begin
                if (x) 
                    next_state = S101;
                else 
                    next_state = IDLE;
            end
            S101: begin
                next_state = IDLE;
            end
            default: next_state = IDLE;
        endcase
    end

    // Output logic
    always @(*) begin
        z = (present_state == S101) ? 1'b1 : 1'b

